    def "Zipping of 2 Optional"() {
        when:
        "Zipping #a with #b"
        def aa = Optionals.zip(a, b)

        then:
        "I should get #zz"
        aa == zz

        where:
        a                  | b                  | zz
        Optional.of("foo") | Optional.of("bar") | Optional.of(new Tuple2<>("foo", "bar"))
        Optional.of("foo") | Optional.empty()   | Optional.empty()
        Optional.empty()   | Optional.of("bar") | Optional.empty()
        Optional.empty()   | Optional.empty()   | Optional.empty()
    }

